<?php/*+------------------------------------------------------------------------------+|     CoreBeat SyStem Manager||     Creator: Gabu||     Revision: v000|     Date: 2013. 05. 24.+------------------------------------------------------------------------------+*/if ( !defined('H-KEI') ) { exit; }checkseo();function checkseo() {	global $database;	if ( CB_IS_SEO == '1' ) {		$seo = $_GET['seo'];		$val = $database->getSelect("row","*","menu"," WHERE `state` = '1' AND `seo_name` = '".$seo."' ");		if ( !empty( $val ) ) {			if ( $val['type'] == 'PAGE' ) {				$_GET['page'] = $val['value'];				$_GET['m'] = $val['id'];			} elseif ( $val['type'] == 'POST' ) {				$_GET['post'] = $val['value'];				$_GET['m'] = $val['id'];			} elseif ( $val['type'] == 'CATEGORY' ) {				$_GET['category'] = $val['value'];				$_GET['m'] = $val['id'];			} elseif ( $val['type'] == 'MODULE' ) {				$m = explode("|",$val['value']);				$_GET['mod'] = $m[0];				$_GET['f'] = $m[1];				$_GET['m'] = $val['id'];			}		}	}}return; ?>